import React, { useState } from 'react'

const Form = props => {

   const initial = { id: null, prod: '', prix: '' }

   const [ user, setUser ] = useState(initial)
         const changed = event => {

            const { name, value } = event.target

            setUser({ ...user, [name]: value })
      }

	return (
		<form id="test"
            onSubmit={event => {
               event.preventDefault()
               if (!user.prod || !user.prix) return

               props.ajouter(user)
               props.setCount(props.count +1)
               setUser(initial)
            }}>
               <label>Produit</label>
               <input type="text" name="prod" value={user.prod} onChange={changed}/>
               <label>Prix</label>
               <input type="text" name="prix"  value={user.prix} onChange={changed}/>
               <a>Ar</a>
               <button id="ajout" className="btn btn-info" onClick="verif();">Ajouter</button>
		</form>
   )
}
export default Form
