import { confirmAlert } from 'react-confirm-alert'; // Import
import React, { useState } from 'react'
import 'react-confirm-alert/src/react-confirm-alert.css' // Import css

const Tableau = props => {

  const initial = { id: null, prod: '', prix: '' }
   const [ user, setUser ] = useState(initial)
         const changed = event => {

            const { name, value } = event.target

            setUser({ ...user, [name]: value })
      }

    const submit = event => {
      event.preventDefault()
        confirmAlert({
          title: <h4>Suppression Produit</h4>,
          message: <h4>{props.user.prod}</h4>,
          buttons: [
            {
              label: 'Oui',
              onClick: () => this.props.deleteUser(use)
            },
            {
              label: 'Non',
            }
          ]
        })

    };

    const submit1 = event => {
      event.preventDefault()
        confirmAlert({
            customUI: ({ onClose }) => {
              return ( 
                <center> 
                <div className="custom-ui" id="popup">
                  <input className="modif" placeholder="this.props.user.prix" onChange={changed}></input><br/><br/>

                  <button className="btn btn-dark"
                    onClick={() => {
                      this.props.editRow(use);
                      onClose();
                    }}
                  >
                    Ok
                  </button><a>&nbsp;&nbsp;</a>
                  <button  className="btn btn-dark" onClick={onClose}>Annuler</button>
                  </div>
                </center>
              );
            }
          })
    };
            return (
                <center>
                    <table class="table table-bordered">
                    <thead>
                        <tr className="tab">
                            <th>id</th>
                            <th>Produits</th>
                            <th>Prix</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.props.users.length > 0 ? (
                        this.props.users.map(user => (
                                <tr key={user.id}>
                                    <td>{user.id}</td>
                                    <td>{user.prod}</td>
                                    <td className="prix">{user.prix}</td>
                                    <td>
                                        <button className="btn btn-danger" onClick={()=>submit(user.id)}>X</button>
                                        <a>&nbsp;</a><button type="submit" className="btn btn-success" onClick={() =>submit1(user.id)}>Edit</button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan={4}></td>
                            </tr>
                        )}
                    </tbody>
                </table>
                <div className="flex-large">
          <button className="btn btn-warning total" data-formTarget="#loginModal" data-toggle="modal">TOTAL</button>
        </div>
        <div className="flex-large total" id="total">
        &nbsp;TOTAL =
        </div>
	</center>
            );

}


export default Tableau;
