import React, { useState } from 'react';

import Form from './Component/forme/forme';
import Tableau from './Component/tableau/tableau';
import Edit from './Component/Edit/Edit'
import './App.css';
import './Component/forme/forme.css'
import './Component/tableau/tableau.css'

const App = () => {
    const usersData = []

    const [ users, setUsers ] = useState(usersData)
    const [count, setCount] = useState(0);

    const ajouter = user => {
      user.id = count 
      setUsers([ ...users, user ])
    }

    const deleteUser = id => {
        setUsers(users.filter(user => user.id !== id))
    }

    const [ editing, setEditing ] = useState(false)

  const initialFormState = { id: null, name: '', username: '' }

  const [ currentUser, setCurrentUser ] = useState(initialFormState)



  const editRow = user => {

    setEditing(true)

    setCurrentUser({ id: user.id, name: user.name, username: user.username })

  }

  const updateUser = (id, updatedUser) => {

    setEditing(false)

    setUsers(users.map(user => (user.id === id ? updatedUser : user)))

  }

    return (
      <div className="container bg-secondary"> 

          <div id="pop_up">
            <Edit editing={editing} setEditing={setEditing} currentUser={currentUser} updateUser={updateUser}/>
          </div>

      <div className="flex-row">
        <div className="flex-large">
          <Form ajouter={ajouter} setCount={setCount} count={count}/>
        </div>
        <div className="flex-large">
          <Tableau users={users} deleteUser={deleteUser} editRow={editRow}/>
        </div>
        </div>

      </div>
    )
  };

export default App;
