import React, { useState } from 'react'

const Form = props => {

   const initial = { id: null, prod: '', prix: '' }

   const [ user, setUser ] = useState(initial)
         const changed = event => {

            const { name, value } = event.target

            setUser({ ...user, [name]: value })
      }

      const verif = user => {
         if (TypeOf(user.prix) !== "number") {
            console.log('Entrer un nombre')
      } else {
         onSubmit=event => {
            event.preventDefault()
            if (!user.prod || !user.prix) return

            props.ajouter(user)
            props.setCount(props.count +1)
            setUser(initial)
         }
      }  
      }
	return (
		<form id="test"
            >
               <label>Produit</label>
               <input type="text" name="prod" value={user.prod} onChange={changed}/>
               <label>Prix</label>
               <input type="text" name="prix"  value={user.prix} onChange={changed}/>
               <label>Ar</label>
               <button id="ajout" className="btn btn-info" onClick={() => {props.verif(user)}}>Ajouter</button>
		</form>
   )
}
export default Form
