import React, { useState } from 'react';

import Form from './Component/forme/forme';
import Tableau from './Component/tableau/tableau';
import './App.css';
import './Component/forme/forme.css'
import './Component/tableau/tableau.css'

const App = () => {
    const usersData = []

    const [ users, setUsers ] = useState(usersData)
    const [count, setCount] = useState(0);
    const [edit, setEdit] = useState(usersData);
    // const [ total, setTotal ] = useState(usersData)
    const ajouter = user => {
      user.id = count 
      setUsers([ ...users, user ])
    }

    // const somme = user => {
    //   var t = 0;
    //   t = t + user.prix;
    //   setUsers([ ...users, user ])
    // }

    const deleteUser = id => {
        setUsers(users.filter(user => user.id !== id))
    }

    const editRow = user => {
      user.prix=edit
      setUsers([ ...users, user ])
      console.log(document.getElementById("entree").value);

    }

    return (
      <form className="container bg-secondary"
          onSubmit={event => {
            event.preventDefault()
            var t = 0;
            for (let i = 0; i<users.length; i++) {
              t= t + users.prix;
            }
            document.getElementById("total").innerHTML=t;
            }}>
      <div className="flex-row">
        <div className="flex-large">
          <Form ajouter={ajouter} setCount={setCount} count={count}/>
        </div>
        <div className="flex-large">
          <Tableau users={users} deleteUser={deleteUser} editRow={editRow} edit={edit} setEdit={setEdit}/>
        </div>
      </div>
    </form>
    )
  };

export default App;
