		<form id="test"
            onSubmit={event => {
               event.preventDefault()
               var valid;
               if (isNaN(user.prix)) {
                  valid="Entrer un nombre";
                  document.getElementById('valid').innerHTML= valid;
               } else {
                  valid="";
               if (!user.prod || !user.prix) return 
