import React, { Component } from "react";
import './liste.css'

class Liste extends Component {

  constructor(props) {
    super(props);
    this.state = { apiResponse: "" };
  }

  callAPI() {
      fetch("http://localhost:8080/List")
          .then(res => res.text())
          .then(res => this.setState({ apiResponse: res }));
  }
  componentWillMount() {
    this.callAPI();
}
transf(){
  var test=this.state.apiResponse
  console.log(JSON.parse(test));
  var test2=JSON.parse(test)
  return test2
}

  render() {
    return (
      <div>    

        <button onClick={()=> {
            var test=this.state.apiResponse
            console.log(JSON.parse(test));
           var test2=JSON.parse(test)


            for(let i=0;i<test2.length;i++){ 

              let h="http://localhost:8080"+test2[i].image
              document.getElementById('va').innerHTML+="<p id='nom'>"+"<p id='im'>"+" <img className='bla' src='"+h+ "'width='50'/>"+"</p>"+ "<br/>" +"Nom :"+test2[i].nom+" "+"<br/>"+"Prenom :"+test2[i].prenom+ "</p>"+"<br/>" 



            }

           }}>Liste</button>



        <div id="va"></div>
        <div id="myCarousel" class="carousel slide" data-ride="carousel">
            <ol class="carousel-indicators">
              {
                this.transf().map((im,index)=>{
                <li data-target="#myCarousel" data-slide-to={index} class="active"></li>
              })}

            </ol>

            <div class="carousel-inner">
              <div class="item active">
              { this.transf().map(im=>{
                <div>
                    <img src={"http://localhost:8080"+im.image} width='50' alt="Los Angeles"/>
                    <div>{im.nom}</div>
                    <div>{im.prenom}</div>
                </div>
              })}
              </div>

            </div>
            <a class="left carousel-control" href="#myCarousel" data-slide="prev">
              <span class="glyphicon glyphicon-chevron-left"></span>
              <span class="sr-only">Previous</span>
            </a>
            <a class="right carousel-control" href="#myCarousel" data-slide="next">
              <span class="glyphicon glyphicon-chevron-right"></span>
              <span class="sr-only">Next</span>
            </a>
        </div>


      </div>
    );
  }
}

export default Liste;
