import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css'



class Liste extends Component {

  constructor(props) {
    super(props);
    this.state = { 
      g:"",
      apiResponse: "" };
  }

  callAPI() {
      fetch("http://localhost:8080/List")
          .then(res => res.text())
          .then(res => this.setState({ apiResponse: res }));
  }
  componentWillMount() {
    this.callAPI();
}
transf(){
  var test=this.state.apiResponse
  var test2=JSON.parse(test)
  var tab=eval('(' + test + ')');  
  this.setState({
    g:tab
  })
}

  render() {
    return (
      <div>    
         <button onClick={()=> {
            var test=this.state.apiResponse
            console.log(JSON.parse(test));
           var test2=JSON.parse(test)
           var tab=eval('(' + test + ')');
           console.log("==",tab);
           this.transf()


            for(let i=0;i<test2.length;i++){ 


              let h="http://localhost:8080"+test2[i].image
              document.getElementById('olona').innerHTML+="<img className='bla' src='"+h+ "'width='50'/>" 
              document.getElementById('olona').innerHTML+="<div>"+test2[i].nom+"</div>"
              document.getElementById('olona').innerHTML+="<div>"+test2[i].prenom+"</div>"
              document.getElementById('olona').innerHTML+='<li data-target="#myCarousel" data-slide-to="'+i+' " class="active">'
            }

           }}>Liste</button>




        <div id="va">

        {this.state.g.length>0?this.state.g.map((im)=>{
                <div>
                    <img src={"http://localhost:8080"+im.image} width='50'/>
                    <div>{im.nom}</div>
                    <div>{im.prenom}</div>
                </div>     
        }  
              ):(<div></div>)}
        </div> 



        <div id="myCarousel" class="carousel slide" data-ride="carousel">

  <ol id ="listra" class="carousel-indicators">
    {/* <li data-target="#myCarousel" data-slide-to="0" class="active"></li>
    <li data-target="#myCarousel" data-slide-to="1"></li>
    <li data-target="#myCarousel" data-slide-to="2"></li> */}
  </ol>


  <div  class="carousel-inner">
    <div id="olona" className="item active"></div>

  </div>


  <a class="left carousel-control" href="#myCarousel" data-slide="prev">
    <span class="glyphicon glyphicon-chevron-left"></span>
    <span class="sr-only">Previous</span>
  </a>
  <a class="right carousel-control" href="#myCarousel" data-slide="next">
    <span class="glyphicon glyphicon-chevron-right"></span>
    <span class="sr-only">Next</span>
  </a>
</div>


      </div>
    );
  }
}

export default Liste;
