import React, { Component } from "react";

class Personne extends Component {
  constructor(props) {
    super(props);
    this.state = { apiResponse: "" };
  }

  callAPI() {
      fetch("http://localhost:8080/Personne")
          .then(res => res.text())
          .then(res => this.setState({ apiResponse: res }));
  }
  componentWillMount() {
    this.callAPI();
}

  render() {
    return (
      <div>    

      <button onClick={()=> {
          var personne=this.state.apiResponse
          console.log(JSON.parse(personne));
         var test2=JSON.parse(personne)


          for(let i=0;i<test2.length;i++){ 
            document.getElementById('va').innerHTML+=test2[i].nom+" "
            document.getElementById('va').innerHTML+=test2[i].prenom+ "<br/>"   

          }

         }}>personne</button>



<button onClick={()=> {
     var personne=this.state.apiResponse
     var test2=JSON.parse(personne)

   test2.sort();
   console.log(test2);
   for(let i=0;i<test2.length;i++){ 
    document.getElementById('vo').innerHTML+=test2[i].nom+" "
    document.getElementById('vo').innerHTML+=test2[i].prenom+ "<br/>"   

  }

         }}>try</button>

      <div id="va"></div>

      <div id="vo"></div>
    </div>
    );
  }
}

export default Personne;
