import React, { Component } from "react";

class Liste extends Component {

  constructor(props) {
    super(props);
    this.state = { apiResponse: "" };
  }

  callAPI() {
      fetch("http://localhost:8080/List")
          .then(res => res.text())
          .then(res => this.setState({ apiResponse: res }));
  }
  componentWillMount() {
    this.callAPI();
}

  render() {
    return (
      <div>    

        <button onClick={()=> {
            var test=this.state.apiResponse
            console.log(JSON.parse(test));

            document.getElementById('va').innerHTML+=this.state.apiResponse
            // for(let i=0;i<this.state.apiResponse.length;i++){ 
            //   document.getElementById('va').innerHTML+=this.state.apiResponse[i].nom
            //   document.getElementById('va').innerHTML+=this.state.apiResponse[i].prenom
            // }

           }}>Liste</button>
        <div id="va"></div>
      </div>
    );
  }
}

export default Liste;
